  const checkMorpheme = (e) => {
    handleMorpheme(searchBox);
    setChecked(true);
  };

  const morpheme = {
    pos: [],
    name: [],
  };

  const morphemeLoad = () => {
    for (let i = 0; i < item.length; i++) {
      if (i % 2 == 0) {
        morpheme.pos.push(item[i]);
      } else {
        morpheme.name.push(item[i]);
      }
    }
  };
