  ));

  return (
    <div className="header">
      <h3 className="title"> ì´ê±´ ë¬´ìŠ¨ ëœ»ì´ì§€? </h3>
      <Grid container spacing={3}>
        <Grid item xs={6}>
          {searchPage}
        </Grid>
        <Grid item xs={6}>
          {!checked ? null : searchSuccess}
        </Grid>
      </Grid>
    </div>
  );
}
